public class E10 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	       double kilometers = 14;
	       double miles = kilometers / 1.6;
	       //seconds to minutes
	       double minutes = 30 / 60;
	       double hours = (minutes + 45) / 60;
	       double milesPerHour = miles / hours;
	       System.out.println("Average speed is " + milesPerHour + " miles per hour.");
	}
}
