package topic01.chapter02;

public class E18 {
// Display a table
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Declare variables
		float a, b;
		
		// Make table
		System.out.println("a\tb\tpow(a,b)");
		a = 1;
		b = 2;
		System.out.println((int)a + "\t" + (int)b + "\t" + (int)Math.pow(a, b));
		a++;
		b++;
		System.out.println((int)a + "\t" + (int)b + "\t" + (int)Math.pow(a, b));
		a++;
		b++;
		System.out.println((int)a + "\t" + (int)b + "\t" + (int)Math.pow(a, b));
		a++;
		b++;
		System.out.println((int)a + "\t" + (int)b + "\t" + (int)Math.pow(a, b));
		a++;
		b++;
		System.out.println((int)a + "\t" + (int)b + "\t" + (int)Math.pow(a, b));
		a++;
		b++;
	}

}
